        self.assertEquals('{"0": 1}', json.dumps({0: 1}, **self.kw))
        self.assertEquals('{"0": 1}', json.dumps({"0": 1}, **self.kw))
        self.assertEquals('{"0": "1"}', json.dumps({"0": "1"}, **self.kw))

